package kr.co.sugarmanager.image.service;

import com.fasterxml.jackson.databind.ObjectMapper;
import kr.co.sugarmanager.image.dto.ImageDTO;
import kr.co.sugarmanager.image.dto.ImageTypeEnum;
import org.junit.jupiter.api.Test;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.context.SpringBootTest;
import static org.assertj.core.api.Assertions.assertThat;

@SpringBootTest
public class ImageServiceTest {
    @Autowired
    private ObjectMapper objectMapper;

    @Test
    void 메시지_역직렬화_성공() throws Exception {
        //given
        String message = "{\"pk\":1,\"imageType\":\"FOOD\",\"extension\":\"jpeg\",\"size\":14936,\"contentType\":null,\"file\":\"\"}";

        //when
        ImageDTO imageDTO = objectMapper.readValue(message, ImageDTO.class);
        //then
        System.out.println("result: "+imageDTO.getImageType());
        assertThat(imageDTO.getImageType()).isNotNull();
    }
}
